INSERT INTO public.campaign_topic(id, content, campaign_id, campaign_topic_type_id) VALUES (NEXTVAL('campaign_topic_seq'), '{"blocks":[{"key":"b90g7","text":"At Paysend we have a clear vision of how to deliver these needs in an instant, freemium, borderless way and designed for ultimate customer satisfaction. We have introduced a global card to card transfer to connect 12 billion cards issued by international systems: Mastercard, Visa, China Union Pay, and local cards. ","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"ae7f9","text":"We launched Paysend Link, where money is sent as a text message with fixed fees. We created a multi-currency global account with a proxy card technology that allows customers to avoid cross border fees and easily manage their money. In June 2019, we plan to launch a new global stablecoin on the Stellar network that will provide a consistent store of value that users can hold or seamlessly transact in real time. ","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"cjqie","text":"Paysend is money FOR the future.","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}}],"entityMap":{}}', 1, 4);
INSERT INTO public.campaign_topic(id, content, campaign_id, campaign_topic_type_id) VALUES (NEXTVAL('campaign_topic_seq'), '{"blocks":[{"key":"gkav","text":"Digital assets are becoming increasingly traded: Daily crypto trading volume went from $20B in Jan ''19 to $100B in May ''19 with a general increase of 25% in prices for key digital assets. A new wave of investors is approaching the market and we are building the Young Ecosystem to help them invest in an easy and intuitive way. With the Young Ecosystem you can:","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"cg1bh","text":"1) Earn our token Young (YNG) with Stepdrop App by simply walking;.","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"5ao2s","text":"2) Easily buy the most trusted cryptocurrencies in less than 30 seconds using Young Platform;.","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"cl1qe","text":"3) Trade with the advanced features and competitive fees on Young Platform Pro.","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"d3mfs","text":"","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"7e2h6","text":"Through its Stepdrop App Young platform has achieved:","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"76ibp","text":"- Over 50k downloads in 4 months","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"10qi8","text":"- An average of 23k active users per month","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"cnm19","text":"- Client acquisition cost of €0,10.","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"5rn4h","text":"","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"4l9u7","text":"Functional, secure and with all the features required by today’s users, we believe Young Platform is what the market was waiting for to become mainstream.","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"7h86j","text":"","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}}],"entityMap":{"0":{"type":"IMAGE","mutability":"MUTABLE","data":{"src":"https://dev02.realmarket.io/api/files/public/GRATPDWODK75TF6ZS7MTJOK2P5KC2KXS.jpg","height":"auto","width":"auto"}}}}', 2, 6);
INSERT INTO public.campaign_topic(id, content, campaign_id, campaign_topic_type_id) VALUES (NEXTVAL('campaign_topic_seq'), '{"blocks":[{"key":"b90g7","text":"Many of us are aware of the crypto revolution that is taking place. In the UK, a survey showed that 77% of people have heard of blockchain and yet only 9% have invested in any crypto assets so far. It can be a world full of jargon, mistrust and disjointed information. ","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"em2o6","text":"This is where Glu comes in. ","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"decn1","text":"Think of us as ''TripAdvisor'' for cryptocurrencies. We are here to help customers discover the world of cryptocurrencies and become more knowledgeable. We believe this will help them make smarter investments in this exciting, but often confusing world. Our free-to-use service will provide information & user-generated reviews on thousands of different crypto platforms and products, with more being added every week. We aim to be the most trusted entry point into the world of cryptocurrencies. This is a market capable of generating significant growth over the next few years. In our view, this is similar to the rapid growth that the internet experienced in the ''90s and Glu is perfectly placed to take advantage. ","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"8l6ro","text":"Please note that the company is incorporated in Luxembourg however all of Glu''s business operations are in the UK.","type":"unstyled","depth":0,"inlineStyleRanges":[{"offset":0,"length":115,"style":"ITALIC"}],"entityRanges":[],"data":{}},{"key":"7h86j","text":"","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}}],"entityMap":{"0":{"type":"IMAGE","mutability":"MUTABLE","data":{"src":"https://dev02.realmarket.io/api/files/public/GRATPDWODK75TF6ZS7MTJOK2P5KC2KXS.jpg","height":"auto","width":"auto"}}}}', 3, 1);
INSERT INTO public.campaign_topic(id, content, campaign_id, campaign_topic_type_id) VALUES (NEXTVAL('campaign_topic_seq'), '{"blocks":[{"key":"b90g7","text":"Many of us are aware of the crypto revolution that is taking place. In the UK, a survey showed that 77% of people have heard of blockchain and yet only 9% have invested in any crypto assets so far. It can be a world full of jargon, mistrust and disjointed information. ","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"em2o6","text":"This is where Glu comes in. ","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"decn1","text":"Think of us as ''TripAdvisor'' for cryptocurrencies. We are here to help customers discover the world of cryptocurrencies and become more knowledgeable. We believe this will help them make smarter investments in this exciting, but often confusing world. Our free-to-use service will provide information & user-generated reviews on thousands of different crypto platforms and products, with more being added every week. We aim to be the most trusted entry point into the world of cryptocurrencies. This is a market capable of generating significant growth over the next few years. In our view, this is similar to the rapid growth that the internet experienced in the ''90s and Glu is perfectly placed to take advantage. ","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"8l6ro","text":"Please note that the company is incorporated in Luxembourg however all of Glu''s business operations are in the UK.","type":"unstyled","depth":0,"inlineStyleRanges":[{"offset":0,"length":115,"style":"ITALIC"}],"entityRanges":[],"data":{}},{"key":"7h86j","text":"","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}}],"entityMap":{"0":{"type":"IMAGE","mutability":"MUTABLE","data":{"src":"https://dev02.realmarket.io/api/files/public/GRATPDWODK75TF6ZS7MTJOK2P5KC2KXS.jpg","height":"au