INSERT INTO public.campaign_topic(id, content, campaign_id, campaign_topic_type_id) VALUES (NEXTVAL('campaign_topic_seq'), '{"blocks":[{"key":"b90g7","text":"Yielders'' award-winning real-estate investment platform is the UK''s first Islamic finance-friendly FinTech company, regulated by the FCA. The rise of FinTech across the world is providing everyday people access to alternative financial opportunities. Our platform aims to deliver consistent returns for the conscientious, modern investor.","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"6s66t","text":"We aim to serve a global demand for alternative and ethical investing - our business model has provided our users with regular rental income consistently on the 1st of every month for 36 months (and counting!) as well as competitive returns on development projects. We provide hassle-free property investments, with predefined lease agreements which offer users a steady income. ","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"2f2t3","text":"Assets are expertly sourced in regions where there is strong projected capital appreciation, allowing investors to benefit from all aspects of a real-estate investment without any of the headaches of being a landlord.","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"7h86j","text":"","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}}],"entityMap":{"0":{"type":"IMAGE","mutability":"MUTABLE","data":{"src":"https://dev02.realmarket.io/api/files/public/GRATPDWODK75TF6ZS7MTJOK2P5KC2KXS.jpg","height":"auto","width":"auto"}}}}', 4, 3 );
INSERT INTO public.campaign_topic(id, content, campaign_id, campaign_topic_type_id) VALUES (NEXTVAL('campaign_topic_seq'), '{"blocks":[{"key":"b90g7","text":"CreditLadder aims to deliver financial fairness to tenants by ensuring their on time rent payment each month builds their Experian credit history. An improved credit history should allow tenants to save money, and ultimately may also help them on to the housing ladder.","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"7h86j","text":"","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"bdhid","text":" ","type":"atomic","depth":0,"inlineStyleRanges":[],"entityRanges":[{"offset":0,"length":1,"key":0}],"data":{}},{"key":"e0som","text":"","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}}],"entityMap":{"0":{"type":"IMAGE","mutability":"MUTABLE","data":{"src":"https://dev02.realmarket.io/api/files/public/WVTQ5TST3YEZCEXW63ED2SO4A74HCKTI.jpg","height":"auto","width":"auto"}}}}', 5, 3);
INSERT INTO public.campaign_topic(id, content, campaign_id, campaign_topic_type_id) VALUES (NEXTVAL('campaign_topic_seq'), '{"blocks":[{"key":"b90g7","text":"Deciwatt develop renewable energy solutions and our new product, NowLight creates instant light and power from the pull of a cord. Just one minute of pulling on NowLight’s cord will generate up to two hours of light and when fully charged NowLight will provide up to 50 hours of light.","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"7h86j","text":"","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}}],"entityMap":{"0":{"type":"IMAGE","mutability":"MUTABLE","data":{"src":"https://dev02.realmarket.io/api/files/public/GRATPDWODK75TF6ZS7MTJOK2P5KC2KXS.jpg","height":"auto","width":"auto"}}}}', 5, 6);
INSERT INTO public.campaign_topic(id, content, campaign_id, campaign_topic_type_id) VALUES (NEXTVAL('campaign_topic_seq'), '{"blocks":[{"key":"fih7a","text":"In April 2018 we ran a crowdfunding campaign on Seedrs, beating our target in a matter of days. Since then we''ve put these funds to good use. In the last year, we’ve...","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"dutfe","text":"","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"asvdl","text":":white_check_mark: Grown revenue 2.5x*.","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"cssr5","text":":white_check_mark: Launched in 12 new cities.","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"646qg","text":":white_check_mark: Grown the number of homes under management from 1,800 to more than 4,000.","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"bjgla","text":"","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"cv800","text":"Our mission is to make hosting hassle-free for everyone. Renting out your home can be a hassle – from dealing with guest inquiries to cleaning and arranging key exchanges – especially if you are away or out of the country yourself. We market our hosts’ homes for short-lets, then look after guests from start to finish.","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"8hbcf","text":"","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}},{"key":"2tgg8","text":"Home sharing is not accessible for many homeowners who don’t have the time or expertise – we’re here to solve that. We host and our clients earn – that''s Airsorted. The extra income & freedom enables our hosts to do more of what they love.","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}}, {"key":"7h86j","text":"","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}}],"entityMap":{"0":{"type":"IMAGE","mutability":"MUTABLE","data":{"src":"https://dev02.realmarket.io/api/files/public/GRATPDWODK75TF6ZS7MTJOK2P5KC2KXS.jpg","height":"auto","width":"auto"}}}}', 6, 7);
INSERT INTO public.campaign_update(id, title, campaign_id, post_date, content) VALUES (NEXTVAL('campaign_update_seq'), 'Campaign Update, June 2019', 2, null, '{"blocks":[{"key":"f9qai","text":"In preparation for the 2019 High-Level Event onStatelessness, two regional preparatory meetings took place. From 12 to 14 November, a Regional Meeting on Statelessness was held in Saly, Senegal, organized by UNHCR and ECOWAS. The discussions focused on the implementation of the Banjul Plan of Action on the Eradication of Statelessness in West Africa and the road towards the 2019 High-Level Event on Statelessness.","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}}],"entityMap":{}}');
INSERT INTO public.campaign_update(id, title, campaign_id, post_date, content) VALUES (NEXTVAL('campaign_update_seq'), '2019 Summer Update', 4, null, '{"blocks":[{"key":"f9qai","text":"Sisters Maha and Souad Mamo were granted Brazilian nationality, with Maha receiving hers during a surprise ceremony that took place at a side event during the 69th session of the UNCHR Executive Committee meeting in Geneva on 4 October. In June, the two young women had been the first to have their statelessness status confirmed under Brazil’s new migration law, which grants protection to stateless persons and provides for residence and simplified naturalization procedures.","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}}],"entityMap":{}}');
INSERT INTO public.campaign_update(id, title, campaign_id, post_date, content) VALUES (NEXTVAL('campaign_update_seq'), 'Campaign soon to be completed!', 6, null, '{"blocks":[{"key":"f9qai","text":"Thanks for all of your amazing support so far. Here’s a quick update on our end. Our team is preparing for our demo-day next week. Here’s a picture of our team test-driving our prototype. If we select your work, we’ll post it on our social channels and send you an extra ticket to our premiere. We know you’re smart because you backed our campaign. We’d love to hear what you like and what we can do better! Please let us know in the comments section on our campaign.","type":"unstyled","depth":0,"inlineStyleRanges":[],"entityRanges":[],"data":{}}],"entityMap":{}}');
